    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        SoccerPlayer item = new SoccerPlayer((int) spnId.getValue(), txtName.getText() , cmbxType.getSelectedItem().toString(), (int ) spnYears.getValue());
        createIteamController itemController = new createIteamController();
        itemController.createNewItem((int) spnId.getValue(), txtName.getText() , cmbxType.getSelectedItem().toString(), (int)spnYears.getValue());
    }//GEN-LAST:event_jButton2ActionPerformed

