    private void itmCloseSessionActionPerformedActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_itmCloseSessionActionPerformedActionPerformed
        int selection;
        selection = JOptionPane.showConfirmDialog(rootPane, "Are you sure?","",JOptionPane.YES_NO_OPTION);
        if (selection == JOptionPane.YES_OPTION){
          FrmLoging frmLogin = new FrmLoging();
          this.setVisible(false);
          frmLogin.setVisible(true);
        
        }
    }//GEN-LAST:event_itmCloseSessionActionPerformedActionPerformed

